var fs = require('fs')
var path = require('path')
var types = ["groovy","ftl","js","widget"]

function file_stat(filename){
	var result = {
		path:filename,
	}
	var stat = fs.statSync(filename)
	if(stat.isDirectory()){
		result.type = 'dir'
	}else {
		var ext = path.extname(filename).substr(1)
		if(types.indexOf(ext)>=0){
			result.type = ext
		}
	}
	result.mtime = stat.mtime
	return result
}

exports.stat = file_stat;